        <p class='planner-choice choice'>Create and manage conferences</p>
        <p class='attendee-choice choice'>Attend and participate in conferences</p>
    </div>
</div>

<style>
    
    :global(body) {
        padding: 0;
        background-color: white;
        font-family: Montserrat;
    }
    
    .container {
        position: relative;
        margin-right: auto;
        margin-left: auto;
        padding: 0;
        width: 100%;
        height: 100vh;
        text-align: center;
        overflow: hidden;
    }

    .content {
        position: relative;
        margin-right: auto;
        margin-left: auto;
        top: 40%;
        transform: translateY(-50%);
        width: 100%;
    }

    .load-animation {
        animation: fadeIn 1s linear;
    }

    h1 {
        padding-top: 10vh;
        margin-bottom: -20vh;
        color: #5C5C5C;
        font-weight: 700;
        font-size: xx-large;
    }

    svg {
        align-content: center;
        margin: auto;
        height: 20vh;
        margin-top: 20vh;
        display: block;
    }

    button {
        width: 25%;
        height: 7vh;
        padding: 1vh;

        color: white;
        font-weight: 700;
        background-color: #4C82F8;
        font-size: medium;
        border-radius: 2vh;
        border-width: 0;
    }

    p {
        color: #BFBFBF;
        font-weight: 700;
        margin-left: 1vw;
        margin-right: 1vw;
    }

    .inline {
        display: inline;
    }

    .choice {
        transform: translateY(-80vh);
        color: #5C5C5C;
    }

    button:hover {
        animation: turnOrange linear 0.3s;
        background-color: #F88A4C;   
    }

    .planner-svg {
        transform: translateY(-40.1vh);
        visibility: hidden;
    }

    .attendee-svg {
        transform: translateY(-80vh);
        visibility: hidden;
    }

    .planner-choice {
        display: none;
    }

    .attendee-choice {
        display: none;
    }

    .default-choice {
        animation: fadeIn linear 0.3s;
    }

    #left:hover ~ .planner-svg {
        visibility: visible;
    }

    #left:hover ~ .planner-choice {
        display: block;
        animation: fadeIn linear 0.3s;
    }

    #left:hover ~ .default-choice {
        display: none;
    }

    #left:hover ~ .group-svg {
        visibility: hidden;
    }

    #right:hover ~ .attendee-svg {
        visibility: visible;
    }

    #right:hover ~ .group-svg {
        visibility: hidden;
    }

    #right:hover ~ .attendee-choice {
        display: block;
        animation: fadeIn linear 0.3s;
    }

    #right:hover ~ .default-choice {
        display: none;
    }

    @keyframes fadeIn {
        0% {opacity:0;}
        100% {opacity:1;}
    }

    @keyframes turnOrange {
        0% { background-color: #4C82F8; }
        100% { background-color: #F88A4C; }
    }
</style>


<script>
    // General imports
    import Router from 'page';

    // Firebase import
    import firebase from "firebase/app";

    // Save choice to firebase
    function writeUserChoice(choice) {
        var user = firebase.auth().currentUser;

        if (choice == 'planner') {
            Router.redirect('/Planner');
        }
        firebase.database().ref('users/' + user.uid).set({
            choice: choice
        });
    }
</script>